import React, { useRef, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./dashboard.module.css";

const spots = [
  {
    name: "Santorini, Greece",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Machu Picchu, Peru",
    image: "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Banff National Park, Canada",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Maldives",
    image: "https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Great Barrier Reef, Australia",
    image: "https://images.unsplash.com/photo-1502082553048-f009c37129b9?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Swiss Alps, Switzerland",
    image: "https://images.unsplash.com/photo-1469474968028-56623f02e42e?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Bali, Indonesia",
    image: "https://images.unsplash.com/photo-1501785888041-af3ef285b470?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Petra, Jordan",
    image: "https://images.unsplash.com/photo-1501594907352-04cda38ebc29?auto=format&fit=crop&w=600&q=80",
  },
  {
    name: "Antelope Canyon, USA",
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Mount Fuji, Japan",
    image: "data:image/jpeg;base64,",
  },
];

const carouselImages = [
  "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1200&q=80",
  "data:image/jpeg;base64,",
  "https://images.unsplash.com/photo-1500534314209-a25ddb2bd429?auto=format&fit=crop&w=1200&q=80",
  "https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1200&q=80",
];

const Dashboard = () => {
  const ferrisRef = useRef();
  const navigate = useNavigate();

  // Carousel state
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % carouselImages.length);
    }, 3500);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const container = ferrisRef.current;
      if (!container) return;
      const tiles = container.querySelectorAll(`.${styles.tile}`);
      const containerRect = container.getBoundingClientRect();
      tiles.forEach((tile) => {
        const tileRect = tile.getBoundingClientRect();
        const tileCenter = tileRect.left + tileRect.width / 2;
        const containerCenter = containerRect.left + containerRect.width / 2;
        const offset = (tileCenter - containerCenter) / containerRect.width;
        tile.style.transform = `perspective(900px) rotateX(${offset * 60}deg) scale(${1 - Math.abs(offset) * 0.4})`;
        tile.style.zIndex = Math.round(100 - Math.abs(offset) * 100);
      });
    };
    const container = ferrisRef.current;
    if (container) {
      container.addEventListener("scroll", handleScroll);
      handleScroll();
    }
    return () => {
      if (container) container.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handlePlanTrip = () => {
    navigate("/PlanTrip");
  };

  return (
    <section className={styles.section}>
      <h1 className={styles.welcomeTitle}>Welcome to Travique</h1>
      <p className={styles.welcomeSubtitle}>
        Discover the worlds most breathtaking destinations, curated just for you. Start your journey with our handpicked recommendations!
      </p>

      {/* Carousel */}
      <div className={styles.bannerSection}>
        <img
          src={carouselImages[current]}
          alt="carousel"
          className={styles.bannerImage}
        />
        <div className={styles.carouselDots}>
          {carouselImages.map((_, idx) => (
            <div
              key={idx}
              className={current === idx ? styles.activeDot : styles.dot}
              onClick={() => setCurrent(idx)}
            />
          ))}
        </div>
      </div>

      <h2 className={styles.heading}>Recommended spots</h2>
      <div className={styles.ferrisContainer} ref={ferrisRef}>
        <div className={styles.ferrisGrid}>
          {spots.map((spot, idx) => (
            <div className={styles.tile} key={idx}>
              <img src={spot.image} alt={spot.name} className={styles.tileImg} />
              <div className={styles.tileName}>{spot.name}</div>
            </div>
          ))}
        </div>
      </div>
      <button className={styles.planTripBtn} onClick={handlePlanTrip}>
        Plan Trip
      </button>
    </section>
  );
};

export default Dashboard;